// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "./ScaleEncoder.sol";

/**
 * @title MultiLocation
 * @notice Library for constructing XCM MultiLocation structures
 * @dev MultiLocation represents a location in the XCM multiverse
 */
library MultiLocation {
    using ScaleEncoder for *;

    /**
     * @notice Destination parachain IDs
     */
    uint32 constant ASSET_HUB = 1000;
    uint32 constant BRIDGE_HUB = 1002;
    uint32 constant ACALA = 2000;
    uint32 constant MOONBEAM = 2004;
    uint32 constant ASTAR = 2006;
    uint32 constant BIFROST = 2030;
    uint32 constant HYDRATION = 2034;

    /**
     * @notice Build a MultiLocation pointing to a parachain
     * @dev Constructs: { parents: 1, interior: X1(Parachain(id)) }
     * @param paraId Parachain ID
     * @return Encoded MultiLocation bytes
     */
    function parachain(uint32 paraId) internal pure returns (bytes memory) {
        // parents = 1
        bytes memory parents = ScaleEncoder.encodeU8(1);

        // interior = X1(Parachain(paraId))
        // X1 variant = 0x01
        // Parachain variant = 0x00
        bytes memory interior = abi.encodePacked(
            hex"01",  // X1
            hex"00",  // Parachain variant
            ScaleEncoder.encodeCompact(paraId)
        );

        return abi.encodePacked(parents, interior);
    }

    /**
     * @notice Build a MultiLocation pointing to an AccountId32
     * @dev Constructs: { parents: 0, interior: X1(AccountId32(id, network: None)) }
     * @param accountId 32-byte account ID
     * @return Encoded MultiLocation bytes
     */
    function accountId32(bytes32 accountId) internal pure returns (bytes memory) {
        // parents = 0
        bytes memory parents = ScaleEncoder.encodeU8(0);

        // interior = X1(AccountId32(id, network: None))
        // X1 variant = 0x01
        // AccountId32 variant = 0x01
        // network = None (0x00)
        bytes memory interior = abi.encodePacked(
            hex"01",  // X1
            hex"01",  // AccountId32 variant
            accountId,
            hex"00"   // network: None
        );

        return abi.encodePacked(parents, interior);
    }

    /**
     * @notice Build a MultiLocation pointing to an AccountKey20 (EVM address)
     * @dev Constructs: { parents: 0, interior: X1(AccountKey20(addr, network: None)) }
     * @param addr 20-byte EVM address
     * @return Encoded MultiLocation bytes
     */
    function accountKey20(address addr) internal pure returns (bytes memory) {
        // parents = 0
        bytes memory parents = ScaleEncoder.encodeU8(0);

        // interior = X1(AccountKey20(addr, network: None))
        // X1 variant = 0x01
        // AccountKey20 variant = 0x03
        // network = None (0x00)
        bytes memory interior = abi.encodePacked(
            hex"01",  // X1
            hex"03",  // AccountKey20 variant
            addr,
            hex"00"   // network: None
        );

        return abi.encodePacked(parents, interior);
    }

    /**
     * @notice Build a MultiLocation for a concrete fungible asset
     * @dev For native token on Polkadot Hub: { parents: 0, interior: Here }
     * @return Encoded MultiLocation bytes
     */
    function nativeAsset() internal pure returns (bytes memory) {
        // parents = 0
        bytes memory parents = ScaleEncoder.encodeU8(0);

        // interior = Here (0x00)
        bytes memory interior = hex"00";

        return abi.encodePacked(parents, interior);
    }

    /**
     * @notice Build a MultiLocation for an asset by ID
     * @dev Constructs: { parents: 0, interior: X2(PalletInstance(50), GeneralIndex(assetId)) }
     * @param assetId Asset ID
     * @return Encoded MultiLocation bytes
     */
    function assetById(uint128 assetId) internal pure returns (bytes memory) {
        // parents = 0
        bytes memory parents = ScaleEncoder.encodeU8(0);

        // interior = X2(PalletInstance(50), GeneralIndex(assetId))
        // X2 variant = 0x02
        // PalletInstance variant = 0x04
        // GeneralIndex variant = 0x07
        bytes memory interior = abi.encodePacked(
            hex"02",  // X2
            hex"04",  // PalletInstance variant
            hex"32",  // pallet index 50 (assets pallet)
            hex"07",  // GeneralIndex variant
            ScaleEncoder.encodeU128(assetId)
        );

        return abi.encodePacked(parents, interior);
    }

    /**
     * @notice Build a MultiLocation for ERC20 token on current chain
     * @dev Maps EVM address to asset location
     * @param token Token address
     * @return Encoded MultiLocation bytes for the asset
     */
    function assetLocation(address token) internal pure returns (bytes memory) {
        // For now, we use a simplified mapping
        // In production, this would query a registry or use a specific encoding
        // This is placeholder logic - actual implementation depends on Hub's asset system
        return nativeAsset();
    }
}

contracts/libs/ScaleEncoder.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

/**
 * @title ScaleEncoder
 * @notice Library for encoding Solidity types into SCALE byte format
 * @dev SCALE (Simple Concatenated Aggregate Little-Endian) codec implementation
 * Reference: https://docs.substrate.io/reference/scale-codec/
 */
library ScaleEncoder {
    /**
     * @notice Encode a uint8 value
     * @param value The value to encode
     * @return Encoded bytes in little-endian format
     */
    function encodeU8(uint8 value) internal pure returns (bytes memory) {
        bytes memory encoded = new bytes(1);
        encoded[0] = bytes1(value);
        return encoded;
    }

    /**
     * @notice Encode a uint32 value in little-endian format
     * @param value The value to encode
     * @return Encoded bytes (4 bytes)
     */
    function encodeU32(uint32 value) internal pure returns (bytes memory) {
        bytes memory encoded = new bytes(4);
        encoded[0] = bytes1(uint8(value));
        encoded[1] = bytes1(uint8(value >> 8));
        encoded[2] = bytes1(uint8(value >> 16));
        encoded[3] = bytes1(uint8(value >> 24));
        return encoded;
    }

    /**
     * @notice Encode a uint64 value in little-endian format
     * @param value The value to encode
     * @return Encoded bytes (8 bytes)
     */
    function encodeU64(uint64 value) internal pure returns (bytes memory) {
        bytes memory encoded = new bytes(8);
        for (uint256 i = 0; i < 8; i++) {
            encoded[i] = bytes1(uint8(value >> (i * 8)));
        }
        return encoded;
    }

    /**
     * @notice Encode a uint128 value in little-endian format
     * @param value The value to encode
     * @return Encoded bytes (16 bytes)
     */
    function encodeU128(uint128 value) internal pure returns (bytes memory) {
        bytes memory encoded = new bytes(16);
        for (uint256 i = 0; i < 16; i++) {
            encoded[i] = bytes1(uint8(value >> (i * 8)));
        }
        return encoded;
    }

    /**
     * @notice Encode a compact integer (variable-length encoding)
     * @dev Compact encoding rules:
     * - 0-63: single byte mode (00)
     * - 64-16383: two-byte mode (01)
     * - 16384-1073741823: four-byte mode (10)
     * - Larger: big-integer mode (11)
     * @param value The value to encode
     * @return Encoded bytes in compact format
     */
    function encodeCompact(uint256 value) internal pure returns (bytes memory) {
        if (value < 64) {
            // Single byte mode
            return abi.encodePacked(uint8(value << 2));
        } else if (value < 16384) {
            // Two-byte mode
            uint16 v = uint16(value << 2) | 1;
            bytes memory encoded = new bytes(2);
            encoded[0] = bytes1(uint8(v));
            encoded[1] = bytes1(uint8(v >> 8));
            return encoded;
        } else if (value < 1073741824) {
            // Four-byte mode
            uint32 v = uint32(value << 2) | 2;
            return encodeU32(v);
        } else {
            // Big-integer mode
            uint256 numBytes = 0;
            uint256 temp = value;
            while (temp > 0) {
                numBytes++;
                temp >>= 8;
            }

            bytes memory encoded = new bytes(numBytes + 1);
            encoded[0] = bytes1(uint8(((numBytes - 4) << 2) | 3));

            for (uint256 i = 0; i < numBytes; i++) {
                encoded[i + 1] = bytes1(uint8(value >> (i * 8)));
            }
            return encoded;
        }
    }

    /**
     * @notice Encode a bytes array with length prefix
     * @param data The bytes to encode
     * @return Encoded bytes with compact length prefix
     */
    function encodeBytes(bytes memory data) internal pure returns (bytes memory) {
        bytes memory length = encodeCompact(data.length);
        return abi.encodePacked(length, data);
    }

    /**
     * @notice Encode a string with length prefix
     * @param str The string to encode
     * @return Encoded bytes
     */
    function encodeString(string memory str) internal pure returns (bytes memory) {
        return encodeBytes(bytes(str));
    }

    /**
     * @notice Encode a MultiLocation structure
     * @param parents Number of parent hops
     * @param interior Encoded interior junctions
     * @return Encoded MultiLocation
     */
    function encodeMultiLocation(uint8 parents, bytes memory interior)
        internal
        pure
        returns (bytes memory)
    {
        return abi.encodePacked(encodeU8(parents), interior);
    }

    /**
     * @notice Concatenate multiple byte arrays
     * @param parts Array of byte arrays to concatenate
     * @return Concatenated bytes
     */
    function concat(bytes[] memory parts) internal pure returns (bytes memory) {
        uint256 totalLength = 0;
        for (uint256 i = 0; i < parts.length; i++) {
            totalLength += parts[i].length;
        }

        bytes memory result = new bytes(totalLength);
        uint256 offset = 0;

        for (uint256 i = 0; i < parts.length; i++) {
            bytes memory part = parts[i];
            for (uint256 j = 0; j < part.length; j++) {
                result[offset + j] = part[j];
            }
            offset += part.length;
        }

        return result;
    }
}

contracts/libs/WeightHelper.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "../interfaces/IXcm.sol";

/**
 * @title WeightHelper
 * @notice Library for XCM weight estimation and management
 * @dev Calls weighMessage on the XCM precompile
 */
library WeightHelper {
    /**
     * @notice XCM precompile address
     */
    address constant XCM_PRECOMPILE = address(0xA0000);

    /**
     * @notice Default weight values for common operations
     */
    uint64 constant DEFAULT_REF_TIME = 1000000000;  // 1 billion
    uint64 constant DEFAULT_PROOF_SIZE = 65536;      // 64 KB

    /**
     * @notice Calculate the weight required for an XCM message
     * @param message Encoded XCM message
     * @return weight Computed weight struct
     */
    function weighMessage(bytes memory message)
        internal
        view
        returns (IXcm.Weight memory weight)
    {
        IXcm xcm = IXcm(XCM_PRECOMPILE);
        return xcm.weighMessage(message);
    }

    /**
     * @notice Estimate fees for a cross-chain transfer
     * @dev This is a simplified estimation - actual fees depend on destination chain
     * @param message Encoded XCM message
     * @return feePas Estimated fee in PAS (native token)
     */
    function estimateFee(bytes memory message)
        internal
        view
        returns (uint256 feePas)
    {
        IXcm.Weight memory weight = weighMessage(message);

        // Simplified fee calculation
        // In production, this should query fee multiplier from chain state
        // Fee = (refTime / 1e9) * base_fee + (proofSize / 1024) * byte_fee
        uint256 refTimeFee = (uint256(weight.refTime) * 100) / 1e9;  // 100 units per billion refTime
        uint256 proofSizeFee = (uint256(weight.proofSize) * 10) / 1024; // 10 units per KB

        return refTimeFee + proofSizeFee;
    }

    /**
     * @notice Create a default weight struct
     * @return weight Default weight
     */
    function defaultWeight() internal pure returns (IXcm.Weight memory weight) {
        weight.refTime = DEFAULT_REF_TIME;
        weight.proofSize = DEFAULT_PROOF_SIZE;
    }

    /**
     * @notice Create a custom weight struct
     * @param refTime Reference time
     * @param proofSize Proof size
     * @return weight Custom weight
     */
    function createWeight(uint64 refTime, uint64 proofSize)
        internal
        pure
        returns (IXcm.Weight memory weight)
    {
        weight.refTime = refTime;
        weight.proofSize = proofSize;
    }

    /**
     * @notice Add a safety margin to weight
     * @param weight Base weight
     * @param marginPercent Margin percentage (e.g., 10 for 10%)
     * @return Adjusted weight with margin
     */
    function addMargin(IXcm.Weight memory weight, uint256 marginPercent)
        internal
        pure
        returns (IXcm.Weight memory)
    {
        IXcm.Weight memory adjusted;
        adjusted.refTime = weight.refTime + uint64((uint256(weight.refTime) * marginPercent) / 100);
        adjusted.proofSize = weight.proofSize + uint64((uint256(weight.proofSize) * marginPercent) / 100);
        return adjusted;
    }
}

